import './App.css';
import { Routes, Route } from "react-router-dom";
import Login from './Login';
import Homepage from './Homepage';
import EvAlis from './EvAlis';
import EvSatis from './EvSatis';
import EvAraci from './EvAraci';
import Ev from './Ev';
import { Link } from 'react-router-dom';
import EvAlici from './EvAlici';
// 1. Importing other modules

function App() {
  /**
  function init() {
    // 2. Load web3
    const Web3 = new GetWeb3();
    this.web3 = await Web3.getWeb3();
    this.setState({ web3: this.web3 });

    // 3. Load Account
    const Account = new GetAccount();
    this.account = await Account.getAccount(this.web3);
    this.setState({ account: this.account[0] });

    // 4. Load Contract
    const Contract = new GetContract();
    this.mainInstance = await Contract.getContract(this.web3, contractJson);
    this.setState({ mainInstance: this.mainInstance });
  }*/

  return (
    <>
    <nav
    className="navbar navbar-dark shadow"
    style={{
      backgroundColor: "#1b2021",
      height: "60px",
      color: "white",
      marginBottom: "10px",
    }}>
    <Link to="/"><b style={{ cursor: "pointer", color: "white" }}>Avalanche Elections</b></Link>
    </nav>
    <Routes>
      <Route path="/" element={<Login />}> </Route>
      <Route path="/evSat/ev" element={<Ev />}> </Route>
      <Route path="/evAraci/ev" element={<Ev />}> </Route>
      <Route path="/evAl/evAl" element={<EvAlici />}> </Route>
      <Route path="/evSat" element={<EvSatis />}> </Route>
      <Route path="/evAl" element={<EvAlis />}> </Route>
      <Route path="/evAraci" element={<EvAraci />}> </Route>
      <Route path="/home" element={<Homepage />}> </Route>
    </Routes>
    </>
  );
}

export default App;
